#!/usr/bin/env python

import argparse
import base64

from Crypto import Random

parser = argparse.ArgumentParser(description="Create a 64k disk image file, with a valid FAT12 FS, filled with mostly random data.", formatter_class=argparse.ArgumentDefaultsHelpFormatter)
parser.add_argument('-f', dest='image_file', nargs='?', default='image.bin', help='Path to image file (will be overwritten).')

args = parser.parse_args()

b64_start = ""

bytes_start = base64.b64decode(b64_start)

with open(args.image_file, 'wb') as f:
    f.write(bytes_start)
    f.write(Random.new().read(65536-len(bytes_start)))
    f.close()

